var colors = require('colors');

function Logger(prefix) {
    //this._prefix = 'âš’ ' + prefix + ' ';
    this._prefix = '';
    this._stream = process.stdout;
    this._error = '';
};

Logger.prototype = {

    write : function(data) {
        this._stream.write(data);
    },

    log : function(data) {
        this.write(this._prefix.grey + data);
    },

    logn : function(data) {
        this.write(this._prefix.grey + data + '\n');
    },

    error : function(data) {
        this._error += data;
        this.write(this._prefix.red + data);
    },

    getError : function() {
        return this._error;
    }

};

module.exports = Logger;
